package com.sergioborne.themoviedb.diimport android.content.Contextimport com.sergioborne.themoviedb.TMDBApplicationimport com.sergioborne.themoviedb.common.SchedulerProviderimport dagger.Moduleimport dagger.Providesimport io.reactivex.Schedulerimport io.reactivex.android.schedulers.AndroidSchedulersimport io.reactivex.schedulers.Schedulersimport javax.inject.Singleton@Moduleclass AppModule {  @Provides  @Singleton  fun provideContext(application: TMDBApplication): Context {    return application.applicationContext  }  @Provides fun provideSchedulerProvider(): SchedulerProvider {    return object : SchedulerProvider {      override fun mainThreadScheduler() = AndroidSchedulers.mainThread()      override fun backgroundThreadScheduler() = Schedulers.io()    }  }}