package com.sergioborne.themoviedb.mainlist.gatewayimport com.sergioborne.themoviedb.common.OutcomeListenerimport com.sergioborne.themoviedb.common.SchedulerProviderimport com.sergioborne.themoviedb.mainlist.network.MoviesServiceimport com.sergioborne.tmdbkotlinchallenge.data.Movieclass MainListGatewayImpl(  private val schedulerProvider: SchedulerProvider,  private val service: MoviesService) : MainListGateway {  override fun loadMovies(listener: OutcomeListener<List<Movie>>) {    service.getPopularMovies().subscribeOn(schedulerProvider.backgroundThreadScheduler()).observeOn(        schedulerProvider.mainThreadScheduler()    ).subscribe(listener::success, listener::error)  }}