package com.sergioborne.themoviedb.mainlist.gatewayimport com.sergioborne.themoviedb.common.OutcomeListenerimport com.sergioborne.themoviedb.common.SchedulerProviderimport com.sergioborne.themoviedb.mainlist.network.MoviesServiceimport com.sergioborne.tmdbkotlinchallenge.data.Pageclass MainListGatewayImpl(  private val schedulerProvider: SchedulerProvider,  private val service: MoviesService) : MainListGateway {  override fun loadMovies(    page: Int,    listener: OutcomeListener<Page>  ) {    service.getPopularMovies(page).subscribeOn(schedulerProvider.backgroundThreadScheduler()).observeOn(        schedulerProvider.mainThreadScheduler()    ).subscribe(listener::success, listener::error)  }}