package com.sergioborne.themoviedb.mainlist.gatewayimport com.sergioborne.themoviedb.common.OutcomeListenerimport com.sergioborne.themoviedb.common.SchedulerProviderimport com.sergioborne.themoviedb.mainlist.network.TvShowsServiceimport com.sergioborne.tmdbkotlinchallenge.data.Pageclass MainListGatewayImpl(  private val schedulerProvider: SchedulerProvider,  private val service: TvShowsService) : MainListGateway {  override fun loadTvShows(    page: Int,    listener: OutcomeListener<Page>  ) {    service.getPopularTvShows(page).subscribeOn(schedulerProvider.backgroundThreadScheduler()).observeOn(        schedulerProvider.mainThreadScheduler()    ).subscribe(listener::success, listener::error)  }}